@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

html {

  /* Colors */
  --color-accent: #154BF4;

  /* Default property values */
  --background: white;
  --color: #222;
  --padding: 2rem; 
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2); 
  --border-radius: 8px; 
  --max-width: 1200px;
  --border-color: #CBCACE;
  --transition-time: 0.1s;
  --transition: var(--transition-time) color,
    var(--transition-time) background-color,
      var(--transition-time) border-color,
        var(--transition-time) text-decoration-color,
          var(--transition-time) box-shadow, var(--transtion-time) transform;
}

.page {
  font-family: system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section .content {
  max-width: var(--max-width);
  margin: 0 auto;
  padding: var(--padding);

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  p {
    padding: 0.25rem 0;
    line-height: 1.5;
  }

  a {
    text-decoration: underline;
  }

  h1 {
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 1rem;
  }

  h2 {
    font-size: 2.25rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
  }

  h3 {
    font-size: 1.75rem; 
    font-weight: 600;
    margin-bottom: 0.25rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container {
  max-width: var(--max-width, 1200px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
  gap: 2rem;

  @media (min-width: 800px) {
    grid-template-columns: 3fr 2fr;
    gap: 14rem;
  }
}

.content {
  display: grid;
  place-content: flex-start;
  gap: 1rem;

  .social-links {
    display: grid;
    gap: 0.5rem;

    a {
      display: flex;
      align-items: center;
      gap: 0.75rem;

      .icon {
        font-size: 1.25rem;
        color: var(--color-accent, rebeccapurple);
        transition: var(--transition, 0.1s transform);
      }

      &:hover .icon {
        transform: scale(1.1);
      }
    }
  }
}

form {
  display: grid;
  gap: 1rem;
  padding: 2rem;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);

  label {
    display: grid;
    gap: 0.25rem;

    span {
      font-size: 0.75rem;
      font-weight: 500;
    }

    input,
    textarea {
      padding: 0.5rem;
      outline-color: transparent;
      transition: var(--transition, 0.1s outline-color);
      border: 1.5px solid var(--border-color, #eee);
      border-radius: var(--border-radius);

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
    }
  }

  .button { /* Defined in Site CSS */
    margin-top: 1rem;
  }
}
